const ArgumentType = require('../../extension-support/argument-type');
const BlockType = require('../../extension-support/block-type');
const Cast = require('../../util/cast');
const Clone = require('../../util/clone');
const Color = require('../../util/color');
const formatMessage = require('format-message');
const MathUtil = require('../../util/math-util');
const RenderedTarget = require('../../sprites/rendered-target');
const log = require('../../util/log');
const StageLayering = require('../../engine/stage-layering');

/**
 * Icon svg to be displayed at the left edge of each extension block, encoded as a data URI.
 * @type {string}
 */
// eslint-disable-next-line max-len
const blockIconURI = 'data:image/svg+xml;base64,';

/**
 * Enum for pen color parameter values.
 * @readonly
 * @enum {string}
 */
const ColorParam = {
    COLOR: 'color',
    SATURATION: 'saturation',
    BRIGHTNESS: 'brightness',
    TRANSPARENCY: 'transparency'
};

/**
 * Host for the Pen-related blocks in Scratch 3.0
 * @param {Runtime} runtime - the runtime instantiating this block package.
 * @constructor
 */
class Scratch3AvatarBlocks {
    constructor (runtime) {
        /**
         * The runtime instantiating this block package.
         * @type {Runtime}
         */
        this.runtime = runtime;
        // runtime.on('targetWasCreated', this._onTargetCreated);
        runtime.on('RUNTIME_DISPOSED', () => this.hide());
    }
    /**
     * The key to load & store a target's pen-related state.
     * @type {string}
     */
    static get STATE_KEY () {
        return 'Scratch.avatar';
    }

    /**
     * When a pen-using Target is cloned, clone the pen state.
     * @param {Target} newTarget - the newly created target.
     * @param {Target} [sourceTarget] - the target used as a source for the new clone, if any.
     * @listens Runtime#event:targetWasCreated
     * @private
     */
    _onTargetCreated (newTarget, sourceTarget) {
        if (sourceTarget) {
            const penState = sourceTarget.getCustomState(Scratch3AvatarBlocks.STATE_KEY);
            if (penState) {
                newTarget.setCustomState(Scratch3AvatarBlocks.STATE_KEY, Clone.simple(penState));
                if (penState.penDown) {
                    newTarget.addListener(RenderedTarget.EVENT_TARGET_MOVED, this._onTargetMoved);
                }
            }
        }
    }

    /**
     * @returns {object} metadata for this extension and its blocks.
     */
    getInfo () {
        return {
            id: 'avatar',
            name: formatMessage({
                id: 'avatar.categoryName',
                default: 'Avatar',
                description: 'Label for the avatar extension category'
            }),
            blockIconURI: blockIconURI,
            blocks: [
                {
                    opcode: 'show',
                    blockType: BlockType.COMMAND,
                    text: formatMessage({
                        id: 'avatar.show',
                        default: 'show 3D scene',
                        description: 'displays the avatar in a 3D scene'
                    })
                },
                {
                    opcode: 'hide',
                    blockType: BlockType.COMMAND,
                    text: formatMessage({
                        id: 'avatar.hide',
                        default: 'hide 3D scene',
                        description: 'hides the avatar in a 3D scene'
                    })
                }
            ],
            menus: {
            }
        };
    }

    show() {
        this.runtime.emit("avatarVisible", true);
    }

    hide() {
        this.runtime.emit("avatarVisible", false);
    }
}

module.exports = Scratch3AvatarBlocks;
